mapper.select:
select

  distinct

'true' as QUERYID,

from sft_inspect_task_item




  order by ${orderByClause}


  limit ${limitStart} , ${count}

mapper.insert:
insert into sft_inspect_task_item (id, gmt_create, gmt_modified, 
  task_code, bg_item_code, result_status, 
  end_date, report_extra, report_time, 
  rig_code, dept_id, rig_id
  )
values (#{id,jdbcType=BIGINT}, #{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModified,jdbcType=TIMESTAMP}, 
  #{taskCode,jdbcType=VARCHAR}, #{bgItemCode,jdbcType=VARCHAR}, #{resultStatus,jdbcType=INTEGER}, 
  #{endDate,jdbcType=TIMESTAMP}, #{reportExtra,jdbcType=VARCHAR}, #{reportTime,jdbcType=TIMESTAMP}, 
  #{rigCode,jdbcType=VARCHAR}, #{deptId,jdbcType=BIGINT}, #{rigId,jdbcType=BIGINT}
  )
