mapper.select:
select
id, gmt_create, gmt_modified, unsafe_action, event_type, site_step, submitter, submit_time, status
from safety.inspection_stopcard


        and id = #{id}


        and gmt_create = #{gmtCreate}


        and gmt_modified = #{gmtModified}


        and unsafe_action = #{unsafeAction}


        and event_type = #{eventType}


        and site_step = #{siteStep}


        and submitter = #{submitter}


        and submit_time = #{submitTime}


        and status = #{status}
