mapper.select:
select
id, gmt_create, gmt_modified, account, name, password, register_time, role, profile_photo, dep_id, dep_name,
position
from safety.user


        and id = #{id}


        and gmt_create = #{gmtCreate}


        and gmt_modified = #{gmtModified}


        and account = #{account}


        and name = #{name}


        and password = #{password}


        and register_time = #{registerTime}


        and role = #{role}


        and profile_photo = #{profilePhoto}


        and dep_id = #{depId}


        and dep_name = #{depName}


        and position = #{position}
